import React, {ChangeEvent, ChangeEventHandler, useState} from 'react';
import { store } from '.';
import './App.css';

function App() {

    const [name, setName] = useState('')

    function addInput(event: ChangeEvent<HTMLInputElement>) {
        console.log(event.currentTarget.value)
        setName(event.currentTarget.value)
    }

    function addTrack() {
        store.dispatch( {type: 'ADD_TRACK', payload: name} )
    }

    return (
        <div className="App">
            <div>
                <input type='text'
                       className='input'
                       // onChange={addInput}
                    onChange={ (event) => addInput(event) }>
                </input>
                <button onClick={addTrack}>Добавить</button>
            </div>
        </div>
    );
}

export default App;
